mapper.insert:
INSERT INTO t_purchase_list
(
purchase_number, amount_paid, amount_payable, purchase_date,
remarks, state,supplier_id
)
VALUES
(
#{purchaseNumber}, #{amountPaid}, #{amountPayable}, #{purchaseDate}, #{remarks},
#{state}, #{supplierId}
)

mapper.select:
SELECT
*
FROM
t_purchase_list t
LEFT JOIN t_user
ON t.user_id = t_user.user_id
LEFT JOIN t_supplier
ON t.supplier_id = t_supplier.supplier_id


        and t.purchase_date => #{sTime}


        and t.purchase_date  <= #{eTime}


        and t.state = #{state}


        and t.return_number = #{returnNumber}


        and t.supplier_id = #{supplierId}
